import React, {useState} from 'react';
import {
    Button,
    CircularProgress,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    Divider
} from "@material-ui/core";
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';

export interface INews {
    id?: number,
    date: string,
    name: string,
    content: string
}

const News = (props: {
    news: INews[],
    isLoading: boolean
    isLogged?: boolean
}) => {
    return (
        <div className="newsBlocks">
            {props.isLoading ?
                <div style={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    margin: "2rem 0"
                }}>
                    <CircularProgress size={50}/>
                </div> :
                props.news.map((elem, index) =>
                    <SeparateNews
                        elem={elem}
                        key={elem.id ? elem.id : index}
                        isLogged={props.isLogged}
                    />
                )}
        </div>
    );
};

const SeparateNews = (props: {
    elem: INews,
    isLogged: boolean | undefined
}) => {
    const {elem} = props;
    const [dialogOpen, setDialogOpen] = useState<boolean>(false);

    const handleDialogOpen = () => {
        setDialogOpen(!dialogOpen);
    }

    const handleDeleteNews = () => {

    }

    return (
        <div
            className="paper border newsBlock"
        >
            <div>
                <p>{elem.date}</p>
                <h3 className="bluePar">{elem.name}</h3>
                {elem.content.split(" ").length - 1 > 22 ?
                    <div>
                        <p>{elem.content.split(" ", 22).map((word, index) =>
                            <span key={index}> {word}</span>
                        )}<span>...</span></p>
                        <Button
                            onClick={handleDialogOpen}
                            variant="contained"
                            style={{margin: "1rem auto", color: "var(--blue)"}}
                            color="default"
                        >Vice info</Button>
                    </div> :
                    <p>{elem.content}</p>
                }
            </div>
            <Dialog open={dialogOpen} onClose={handleDialogOpen} className="dialog newsDialog">
                <DialogTitle>{elem.name}</DialogTitle>
                <Divider/>
                <DialogContent>
                    <p>{elem.content}</p>
                    <p>{elem.date}</p>
                    {props.isLogged ?
                        <div>
                            <Button
                                style={{backgroundColor: "white", color: "var(--fiolet)"}}
                                onClick={handleDeleteNews}
                            >
                                <DeleteForeverIcon fontSize="small" />
                            </Button>
                        </div> : null}
                </DialogContent>
                <Divider/>
                <DialogActions>
                    <Button
                        onClick={handleDialogOpen}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Zavřít</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}

export default News;
