import * as React from 'react';
import {useContext, useEffect, useState} from "react";
import LanguageContext from "../../Context/LanguageContext";
import moment from "moment";
import {Button, CircularProgress} from "@material-ui/core";
import {Link} from 'react-router-dom';
import News from "../Common/News";
import {INews} from "../Common/News";

const ActualNews = () => {
    const [news, setNews] = useState<Array<INews>>([]);
    const [isLoading, setLoading] = useState<boolean>(true);
    const language = useContext(LanguageContext);

    useEffect(() => {
        getNews();
    }, []);

    const getNews = () => {
        setTimeout(() => {
            fetch('http://localhost:3000/topNews')
                .then(response => {
                    return response.json();
                })
                .then(data => {
                    setNews(
                        data.map((row: INews) => {
                            return {
                                id: row.id,
                                date: moment(row.date).format('DD.MM.YYYY'),
                                name: row.name,
                                content: `${row.content}. ${row.content}. ${row.content}. ${row.content}.`
                            }
                        })
                    );
                    setLoading(false);
                })
        }, 5000);
    }

    return (
        news !== [] ? (
            <div className="actualNews padding">
                <h1>{language.value === 'CZ' ? "Novinky" : "Actual news"}</h1>
                {isLoading ?
                    <div>
                        <CircularProgress style={{margin: "0 auto"}}/>
                    </div> :
                    <News news={news}/>
                }
                {news.length > 0 ?
                    <div className="allNews">
                        <Button
                            variant="contained"
                            style={{margin: "1rem auto 0 auto", color: "var(--blue)"}}
                            component={Link}
                            to="/news"
                        >VÅ¡echny novinky</Button>
                    </div> :
                    !isLoading &&
                    <div>
                        <p>Zadne novinky nejsou</p>
                    </div>
                }
            </div>
        ) : null
    );
};

export default ActualNews;
