import {
    Formik,
    FormikHelpers,
    FormikProps,
    Form,
    Field,
    FieldProps,
} from 'formik';
import * as yup from 'yup';
import {Button, TextField} from "@material-ui/core";
import React from "react";
import {IUser} from "../MainContent/AllNews";

const LoginSchema = yup.object().shape({
    login: yup.string().min(3, "Too short").max(200, "Too long").required("Required"),
    password: yup.string().min(3, "Too short").max(200, "Too long").required("Required")
});

const LoginForm = (props: { user: IUser, setUser: (value: IUser)=>void }) => {
    return (
        <div className="loginContainer">
            <Formik
                initialValues={props.user}
                validationSchema={LoginSchema}
                onSubmit={(
                    values: IUser,
                    actions
                ) => {
                    props.setUser(values);
                    actions.setSubmitting(false);
                }}
            >
                {(props: FormikProps<IUser>) => {
                    const {
                        values,
                        touched,
                        errors,
                        handleBlur,
                        handleChange,
                        isSubmitting
                    } = props;
                    return (
                        <Form className="form">
                            <p className="titleSecond">Uzivatelske jmeno</p>
                            <TextField
                                type="text"
                                name="login"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.login}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={
                                    errors.login && touched.login ?
                                        errors.login :
                                        "Enter login"
                                }
                                error={
                                    !!(errors.login && touched.login)
                                }
                            />
                            {/*{errors.login && touched.login ? (*/}
                            {/*    <p className="error">{errors.login}</p>*/}
                            {/*) : null}*/}

                            <p className="titleSecond">Heslo</p>
                            <TextField
                                type="text"
                                name="password"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                            {errors.password && touched.password ? (
                                <p className="error">{errors.password}</p>
                            ) : null}

                            <div className="buttons">
                                <Button
                                    type="submit"
                                    variant="contained"
                                    style={{margin: "0.5rem", color: "var(--blue)"}}
                                    color="default"
                                >Submit</Button>
                            </div>
                        </Form>
                    );
                }}
            </Formik>
        </div>
    );
}

export default LoginForm;
