import * as React from 'react';
import {useContext, useEffect, useState} from "react";
import LanguageContext from "../../Context/LanguageContext";
import moment from "moment";
import {Button, Dialog, DialogActions, DialogContent, DialogTitle, Paper} from "@material-ui/core";

interface INews {
    id: number,
    date: string,
    name: string,
    content: string
}

const ActualNews = () => {
    const [news, setNews] = useState<Array<INews> | null>(null);
    const [dialogOpen, setDialogOpen] = useState<boolean>(false);
    const language = useContext(LanguageContext);

    const handleDialogOpen = () => {
        setDialogOpen(!dialogOpen);
    }

    useEffect(() => {
        getNews();
    }, []);

    const getNews = () => {
        fetch('http://localhost:3000/topNews')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setNews(
                    data.map((row: INews) => {
                        return {
                            id: row.id,
                            date: moment(row.date).format('DD.MM.YYYY'),
                            name: row.name,
                            content: row.content
                        }
                    })
                );
            })
    }

    return (
        news !== null ? (
            <div className="actualNews padding">
                <h1>{language.value === 'CZ' ? "Novinky" : "Actual news"}</h1>
                <div className="newsBlocks">
                    {news.map(elem =>
                        <div
                            className="paper border newsBlock"
                            key={news.indexOf(elem)}
                        >
                            <div>
                                <p>{elem.date}</p>
                                <h3 className="bluePar">{elem.name}</h3>
                                <p>{elem.content.split(".", 2).map(sentence =>
                                    <span key={elem.content.indexOf(sentence)}> {sentence}.</span>
                                )}<span>..</span></p>
                                <Button
                                    onClick={handleDialogOpen}
                                    variant="contained"
                                    style={{margin: "1rem auto", color: "var(--blue)"}}
                                    color="default"
                                >Vice info</Button>
                            </div>
                            <Dialog open={dialogOpen} onClose={handleDialogOpen}>
                                <DialogTitle>{elem.name}</DialogTitle>
                                <DialogContent>{elem.content}</DialogContent>
                                <DialogActions>
                                    <Button
                                        onClick={handleDialogOpen}
                                        variant="contained"
                                        style={{margin: "1rem auto", color: "var(--blue)"}}
                                        color="default"
                                    >Zavřít</Button>
                                </DialogActions>
                            </Dialog>
                        </div>
                    )}
                </div>
                <div className="allNews">
                    <Button
                        variant="contained"
                        style={{margin: "1rem auto", color: "var(--blue)"}}
                    >Všechny novinky</Button>
                </div>
            </div>
        ) : null
    );
};

export default ActualNews;
