import {Button, CircularProgress, TextField} from "@material-ui/core";
import React from "react";
import {
    Formik,
    FormikProps,
    Form,
} from 'formik';
import * as yup from 'yup';
import {INews} from "./News";
import moment from "moment";
import {toast, ToastContainer} from "react-toastify";

const InputNewsSchema = yup.object().shape({
    name: yup.string().min(3, "Too short").max(250, "Too long").required("Required"),
    content: yup.string().min(3, "Too short").max(3000, "Too long").required("Required")
});

const InputNewsForm = (props: { news: INews, setNews: (value: INews) => void }) => {
    const addNews = (values: INews) => {
        return new Promise(((resolve) => {
            fetch('http://localhost:3000/addNews', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(values),
            })
                .then(response => response.json())
                .then(data => resolve(data));
        }))
    }

    return (
        <div className="inputContainer">
            <Formik
                initialValues={props.news}
                validationSchema={InputNewsSchema}
                onSubmit={(values: INews, actions) => {
                    addNews({...values, date: moment().format('YYYY.MM.DD')})
                        .then(res => {
                        if (typeof res === "boolean") {
                            toast.success("Ulozeno", {
                                position: "bottom-center",
                                autoClose: 5000,
                                hideProgressBar: false,
                                closeOnClick: true,
                                pauseOnHover: true,
                                draggable: true,
                                progress: undefined,
                            });
                        } else {
                            toast.success("Neco se nepovedlo!", {
                                position: "bottom-center",
                                autoClose: 5000,
                                hideProgressBar: false,
                                closeOnClick: true,
                                pauseOnHover: true,
                                draggable: true,
                                progress: undefined,
                            });
                        }
                    });
                    actions.setSubmitting(false);
                }}
            >
                {(props: FormikProps<INews>) => {
                    const {
                        values,
                        touched,
                        errors,
                        handleBlur,
                        handleChange,
                        isSubmitting
                    } = props;
                    return (
                        <Form className="form">
                            <p className="titleSecond">Nazev novinky</p>
                            <TextField
                                type="text"
                                name="name"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.name}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.name && touched.name && errors.name}
                                error={!!(errors.name && touched.name)}
                            />

                            <p className="titleSecond">Obsah novinky</p>
                            <TextField
                                type="text"
                                name="content"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                multiline={true}
                                value={values.content}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.content && touched.content && errors.content}
                                error={!!(errors.content && touched.content)}
                            />

                            {isSubmitting ?
                                <div style={{
                                    display: "flex",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    margin: "2rem 0"
                                }}>
                                    <CircularProgress size={50}/>
                                </div> :
                                <div className="buttons">
                                    <Button
                                        type="submit"
                                        variant="contained"
                                        style={{margin: "0.5rem", color: "var(--blue)"}}
                                        color="default"
                                    >Nahrat</Button>
                                </div>}
                        </Form>
                    );
                }}
            </Formik>
            {/*<ToastContainer*/}
            {/*    position="bottom-center"*/}
            {/*    autoClose={5000}*/}
            {/*    hideProgressBar={false}*/}
            {/*    newestOnTop={false}*/}
            {/*    closeOnClick*/}
            {/*    rtl={false}*/}
            {/*    pauseOnFocusLoss*/}
            {/*    draggable*/}
            {/*    pauseOnHover*/}
            {/*/>*/}
        </div>
    );
}

export default InputNewsForm;
