import {
    Formik,
    FormikHelpers,
    FormikProps,
    Form,
    Field,
    FieldProps,
} from 'formik';
import {Button, TextField} from "@material-ui/core";
import React from "react";
import {IUser} from "../MainContent/AllNews";

const LoginForm = (props: { user: IUser }) => {
    return (
        <div className="loginContainer">
            <Formik
                initialValues={props.user}
                onSubmit={(
                    values: IUser,
                    actions
                ) => {
                    alert(JSON.stringify(values, null, 2));
                    actions.setSubmitting(false);
                }}
            >
                {(props: FormikProps<IUser>) => {
                    const {
                        values,
                        touched,
                        errors,
                        handleBlur,
                        handleChange,
                        isSubmitting
                    } = props;
                    return (
                        <Form className="form">
                            <p className="titleSecond">Uzivatelske jmeno</p>
                            <TextField
                                type="text"
                                name="login"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.login}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />

                            <p className="titleSecond">Heslo</p>
                            <TextField
                                type="text"
                                name="password"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />

                            <div>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    style={{margin: "0.5rem", color: "var(--blue)"}}
                                    color="default"
                                >Submit</Button>
                            </div>
                        </Form>
                    );
                }}
            </Formik>
        </div>
    );
}

export default LoginForm;
