import {Button, TextField} from "@material-ui/core";
import React from "react";
import {
    Formik,
    FormikProps,
    Form,
} from 'formik';
import * as yup from 'yup';
import {INews} from "./News";

const InputNewsSchema = yup.object().shape({
    name: yup.string().min(3, "Too short").max(250, "Too long").required(),
    content: yup.string().min(3, "Too short").max(3000, "Too long").required()
});

const InputNewsForm = (props: { news: INews, setNews: (value: INews) => void }) => {
    return (
        <div className="inputContainer">
            <Formik
                initialValues={props.news}
                onSubmit={(values => {
                    console.log(values);
                })}
            >

            </Formik>


            <div>
                <p className="titleSecond">Nazev novinky</p>
                <TextField
                    fullWidth={true}
                    variant="outlined"
                    size="small"
                />
            </div>
            <div>
                <p className="titleSecond">Obsah novinky</p>
                <TextField
                    fullWidth={true}
                    variant="outlined"
                    size="small"
                    multiline={true}
                />
            </div>
            <Button
                // onClick={}
                variant="contained"
                style={{margin: "0.5rem", color: "var(--blue)"}}
                color="default"
            >Vstopit</Button>
        </div>
    );
}

export default InputNewsForm;
