import {
    Formik,
    FormikProps,
    Form,
} from 'formik';
import * as yup from 'yup';
import {Button, CircularProgress, TextField} from "@material-ui/core";
import React, {useState} from "react";
import {toast} from "react-toastify";

export interface IUser {
    login: string,
    password: string
}

const LoginSchema = yup.object().shape({
    login: yup.string().min(3, "Too short").max(200, "Too long").required("Required"),
    password: yup.string().min(3, "Too short").max(200, "Too long").required("Required")
});

interface ILoginForm {
    user: IUser,
    setUser: (value: IUser) => void,
    isLogged: boolean,
    setIsLogged: (value: boolean) => void
}

const LoginForm = (props: ILoginForm) => {
    const {login, password} = props.user;
    const [wrongLogin, setWrongLogin] = useState<boolean>(false);

    const getLoggedIn = (value: IUser) => {
        return new Promise(((resolve) => {
            fetch('http://localhost:3000/checkUser', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(value),
            })
                .then(response => response.json())
                .then(data => resolve(data));
        }))
    }

    return (
        <div className="loginContainer">
            <Formik
                initialValues={{login, password}}
                validationSchema={LoginSchema}
                onSubmit={(
                    values: IUser,
                    actions
                ) => {
                    getLoggedIn(values).then(res => {
                        if (res) {
                            props.setUser(values);
                            props.setIsLogged(true);
                            setWrongLogin(false);
                        } else {
                            setWrongLogin(true);
                        }
                        toast.success("Povedlo se");
                        actions.setSubmitting(false);
                    });
                }}
            >
                {(props: FormikProps<IUser>) => {
                    const {
                        values,
                        touched,
                        errors,
                        handleBlur,
                        handleChange,
                        isSubmitting
                    } = props;
                    return (
                        <Form className="form">
                            <p className="titleSecond">Uzivatelske jmeno</p>
                            <TextField
                                type="text"
                                name="login"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.login}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.login && touched.login && errors.login}
                                error={!!(errors.login && touched.login)}
                            />

                            <p className="titleSecond">Heslo</p>
                            <TextField
                                type="text"
                                name="password"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.password && touched.password && errors.password}
                                error={!!(errors.password && touched.password)}
                            />

                            {isSubmitting ?
                                <div style={{
                                    display: "flex",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    margin: "2rem 0"
                                }}>
                                    <CircularProgress size={50}/>
                                </div> :
                                <div className="buttons">
                                    <Button
                                        type="submit"
                                        variant="contained"
                                        style={{margin: "0.5rem", color: "var(--blue)"}}
                                        color="default"
                                    >Vstopit</Button>
                                </div>}
                        </Form>
                    );
                }}
            </Formik>
            {wrongLogin ?
                <h3 className="error">Spanty login nebo heslo</h3>
                : null}
        </div>
    );
}

export default LoginForm;
