import {Button, CircularProgress, TextField} from "@material-ui/core";
import React from "react";
import {
    Formik,
    FormikProps,
    Form,
} from 'formik';
import * as yup from 'yup';
import {INews} from "./News";

const InputNewsSchema = yup.object().shape({
    name: yup.string().min(3, "Too short").max(250, "Too long").required(),
    content: yup.string().min(3, "Too short").max(3000, "Too long").required()
});

const InputNewsForm = (props: { news: INews, setNews: (value: INews) => void }) => {
    return (
        <div className="inputContainer">
            <Formik
                initialValues={props.news}
                validationSchema={InputNewsSchema}
                onSubmit={(values: INews, actions) => {
                    console.log(values);
                    // props.setNews(values);
                    actions.setSubmitting(false);
                }}
            >
                {(props: FormikProps<INews>) => {
                    const {
                        values,
                        touched,
                        errors,
                        handleBlur,
                        handleChange,
                        isSubmitting
                    } = props;
                    return (
                        <Form>
                            <p className="titleSecond">Nazev novinky</p>
                            <TextField
                                type="text"
                                name="name"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.name}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.name && touched.name && errors.name}
                                error={!!(errors.name && touched.name)}
                            />

                            <p className="titleSecond">Obsah novinky</p>
                            <TextField
                                type="text"
                                name="content"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                multiline={true}
                                value={values.content}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.content && touched.content && errors.content}
                                error={!!(errors.content && touched.content)}
                            />

                            {isSubmitting ?
                                <div style={{
                                    display: "flex",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    margin: "2rem 0"
                                }}>
                                    <CircularProgress size={50}/>
                                </div> :
                                <div className="buttons">
                                    <Button
                                        type="submit"
                                        variant="contained"
                                        style={{margin: "0.5rem", color: "var(--blue)"}}
                                        color="default"
                                    >Nahrat</Button>
                                </div>}
                        </Form>
                    );
                }}
            </Formik>
        </div>
    );
}

export default InputNewsForm;
