import React, {useEffect, useState} from 'react';
import News, {INews} from "../Common/News";
import moment from "moment";
import {VpnKey} from "@material-ui/icons";
import {
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle, Divider,
    TextField
} from "@material-ui/core";
import LoginForm from "../Common/LoginForm";

export interface IUser {
    login: string,
    password: string
}

const AllNews = () => {
    const [news, setNews] = useState<Array<INews>>([]);
    const [isLoading, setIsLoading] = useState<boolean>(true);

    useEffect(() => {
        getAllNews();
    }, []);

    const getAllNews = () => {
        fetch('http://localhost:3000/allNews')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setNews(
                    data.map((row: INews) => {
                        return {
                            id: row.id,
                            date: moment(row.date).format('DD.MM.YYYY'),
                            name: row.name,
                            content: row.content
                        }
                    })
                );
                setIsLoading(false);
            })
    }

    return (
        <div className="allNews padding">
            <h1 className="mainLabel">Novinky</h1>
            <News news={news} isLoading={isLoading}/>
            <AddNews/>
        </div>
    );
};

const AddNews = () => {
    const [openDialog, setOpenDialog] = useState<boolean>(false);
    const [user, setUser] = useState<IUser>({login: "", password: ""});
    const [isLogged, setIsLogged] = useState<boolean>(false);

    const handleDialogOpen = () => {
        setOpenDialog(!openDialog);
    }

    useEffect(() => {
        fetch('http://localhost:3000/checkUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        })
            .then(response => response.json())
            .then(data => setIsLogged(data));
    }, [user]);

    return (
        <div className="addNews">
            <div>
                <Button
                    style={{backgroundColor: "white", color: "var(--fiolet)"}}
                    onClick={handleDialogOpen}
                >
                    <VpnKey/>
                </Button>
            </div>
            <Dialog open={openDialog} onClose={handleDialogOpen} className="dialog addNewsDialog">
                <DialogTitle>Pridat dalsi novinku</DialogTitle>
                <Divider />
                <DialogContent className="dialogContent">
                    {isLogged ? <InputNews/> : <LoginForm user={user} setUser={setUser}/>}
                </DialogContent>
                <Divider />
                <DialogActions className="buttons">
                    <Button
                        onClick={handleDialogOpen}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Zavrit</Button>
                    <Button
                        onClick={() => setIsLogged(false)}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Vystoupit</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

const InputNews = () => {
    return (
        <div className="inputContainer">
            <div>
                <p className="titleSecond">Nazev novinky</p>
                <TextField
                    fullWidth={true}
                    variant="outlined"
                    size="small"
                />
            </div>
            <div>
                <p className="titleSecond">Obsah novinky</p>
                <TextField
                    fullWidth={true}
                    variant="outlined"
                    size="small"
                    multiline={true}
                />
            </div>
            <Button
                // onClick={}
                variant="contained"
                style={{margin: "0.5rem", color: "var(--blue)"}}
                color="default"
            >Vstopit</Button>
        </div>
    );
}

export default AllNews;
