import React, {useEffect, useState} from 'react';
import News, {INews} from "../Common/News";
import moment from "moment";
import {VpnKey} from "@material-ui/icons";
import {Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField} from "@material-ui/core";

const AllNews = () => {
    const [news, setNews] = useState<Array<INews>>([]);
    const [isLoading, setIsLoading] = useState<boolean>(true);

    useEffect(() => {
        getAllNews();
    }, []);

    const getAllNews = () => {
        fetch('http://localhost:3000/allNews')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setNews(
                    data.map((row: INews) => {
                        return {
                            id: row.id,
                            date: moment(row.date).format('DD.MM.YYYY'),
                            name: row.name,
                            content: row.content
                        }
                    })
                );
                setIsLoading(false);
            })
    }

    return (
        <div className="allNews padding">
            <h1 className="mainLabel">Novinky</h1>
            <News news={news} isLoading={isLoading}/>
            <AddNews/>
        </div>
    );
};

interface IUser {
    login: string,
    password: string
}

const AddNews = () => {
    const [openDialog, setOpenDialog] = useState<boolean>(false);
    const [user, setUser] = useState<IUser | null>({login: "admin", password: "admin"});
    const [isLogged, setIsLogged] = useState<boolean>(false);

    const handleDialogOpen = () => {
        setOpenDialog(!openDialog);
    }

    const login = () => {
        fetch('http://localhost:3000/checkUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setIsLogged(data);
            });
    }

    return (
        <div className="addNews">
            <div>
                <Button
                    style={{backgroundColor: "white", color: "var(--fiolet)"}}
                    onClick={handleDialogOpen}
                >
                    <VpnKey/>
                </Button>
            </div>
            <Dialog open={openDialog} onClose={handleDialogOpen} className="dialog">
                <DialogTitle>Pridat dalsi novinku</DialogTitle>
                <DialogContent>
                    {isLogged ? <InputNews/> : <LoginForm/>}
                </DialogContent>
                <DialogActions>

                </DialogActions>
            </Dialog>
        </div>
    );
};

const InputNews = () => {
    return(
        <div className="inputContainer">
            <div>
                <p className="titleSecond">Nazev novinky</p>
                <TextField/>
            </div>
            <div>
                <p className="titleSecond">Obsah novinky</p>
                <TextField/>
            </div>
        </div>
    );
}

const LoginForm = () => {
    return (
        <div className="loginContainer">
            <div>
                <p className="titleSecond">Login</p>
                <TextField>

                </TextField>
            </div>
            <div>
                <p className="titleSecond">Heslo</p>
                <TextField>

                </TextField>
            </div>
        </div>
    );
}

export default AllNews;
