import * as React from 'react';
import {useContext, useEffect, useState} from "react";
import LanguageContext from "../../Context/LanguageContext";
import moment from "moment";
import {Button} from "@material-ui/core";

interface INews {
    id: number,
    date: string,
    name: string,
    content: string
}

const ActualNews = () => {
    const [news, setNews] = useState<Array<INews> | null>(null);
    const language = useContext(LanguageContext);

    useEffect(() => {
        getNews();
    }, []);

    useEffect(() => {
        let number = 3;
        if (news !== null) {
            news.length % 2 === 0 && (number = 2);
        }
    }, [news]);

    const getNews = () => {
        fetch('http://localhost:3000/news')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setNews(
                    data.map((row: INews) => {
                        return {
                            id: row.id,
                            date: moment(row.date).format('DD.MM.YYYY'),
                            name: row.name,
                            content: row.content
                        }
                    })
                );
            })
    }

    return (
        news !== null ? (
            <div className="actualNews padding">
                <h1>{language.value === 'CZ' ? "Novinky" : "Actual news"}</h1>
                <div className="newsBlocks">
                    {news.map(elem =>
                        <div className="newsBlock border" key={news.indexOf(elem)}>
                            <div className="newsBlockContentLink">
                                <div className="newsBlockContent">
                                    <div className="dateOfPublication">
                                        <p>{elem.date.replace(/-/g, '.')}</p>
                                    </div>
                                    <div className="newsName">
                                        <h3>{elem.name}</h3>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
                <div className="allNews">
                    <Button
                        variant="contained"
                    >VÅ¡echny novinky</Button>
                </div>
            </div>
        ) : null
    );
};

export default ActualNews;
