import React, {useEffect, useState} from 'react';
import News, {INews} from "../Common/News";
import moment from "moment";
import {VpnKey} from "@material-ui/icons";
import {
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    TextField
} from "@material-ui/core";
import {
    Formik,
    FormikHelpers,
    FormikProps,
    Form,
    Field,
    FieldProps,
} from 'formik';

const AllNews = () => {
    const [news, setNews] = useState<Array<INews>>([]);
    const [isLoading, setIsLoading] = useState<boolean>(true);

    useEffect(() => {
        getAllNews();
    }, []);

    const getAllNews = () => {
        fetch('http://localhost:3000/allNews')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setNews(
                    data.map((row: INews) => {
                        return {
                            id: row.id,
                            date: moment(row.date).format('DD.MM.YYYY'),
                            name: row.name,
                            content: row.content
                        }
                    })
                );
                setIsLoading(false);
            })
    }

    return (
        <div className="allNews padding">
            <h1 className="mainLabel">Novinky</h1>
            <News news={news} isLoading={isLoading}/>
            <AddNews/>
        </div>
    );
};

interface IUser {
    login: string,
    password: string
}

const AddNews = () => {
    const [openDialog, setOpenDialog] = useState<boolean>(false);
    const [user, setUser] = useState<IUser>({login: "", password: ""});
    const [isLogged, setIsLogged] = useState<boolean>(false);

    const handleDialogOpen = () => {
        setOpenDialog(!openDialog);
    }

    const login = () => {
        fetch('http://localhost:3000/checkUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setIsLogged(data);
            });
    }

    return (
        <div className="addNews">
            <div>
                <Button
                    style={{backgroundColor: "white", color: "var(--fiolet)"}}
                    onClick={handleDialogOpen}
                >
                    <VpnKey/>
                </Button>
            </div>
            <Dialog open={openDialog} onClose={handleDialogOpen} className="dialog addNewsDialog">
                <DialogTitle>Pridat dalsi novinku</DialogTitle>
                <DialogContent className="dialogContent">
                    {isLogged ? <InputNews /> : <LoginForm user={user}/>}
                </DialogContent>
                <DialogActions className="buttons">


                    {
                        <Button
                            onClick={login}
                            variant="contained"
                            style={{margin: "0.5rem", color: "var(--blue)"}}
                            color="default"
                        >Vstopit</Button>
                    }


                    <Button
                        onClick={handleDialogOpen}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Zavrit</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

const InputNews = () => {
    return (
        <div className="inputContainer">
            <div>
                <p className="titleSecond">Nazev novinky</p>
                <TextField
                    fullWidth={true}
                    variant="outlined"
                    size="small"
                />
            </div>
            <div>
                <p className="titleSecond">Obsah novinky</p>
                <TextField
                    fullWidth={true}
                    variant="outlined"
                    size="small"
                    multiline={true}
                />
            </div>
        </div>
    );
}

const LoginForm = (props: {user: IUser}) => {
    return (
        <div className="loginContainer">
            <Formik
                initialValues={props.user}
                validate={values => {
                    const errors: IUser = {login: "", password: ""};
                    if (!values.login) errors.login = "Required";
                    else if (!values.password) errors.password = "Required";
                    return errors;
                }}
                onSubmit={(values, actions) => {
                    console.log({values, actions});
                    alert(JSON.stringify(values, null, 2));
                    actions.setSubmitting(false);
                }}
            >
                <Form>
                    <label htmlFor="login" className="titleSecond">Uzivatelske jmeno</label>
                    <Field id="login" name="login" placeholder="Uzivatelske jmeno" />
                    <label htmlFor="password" className="password">Heslo</label>
                    <Field id="password" name="password" placeholder="Heslo" />
                    <Button
                        // onClick={}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Poslat</Button>
                </Form>
            </Formik>
            {/*<div>*/}
            {/*    <p className="titleSecond">Login</p>*/}
            {/*    <TextField*/}
            {/*        fullWidth={true}*/}
            {/*        variant="outlined"*/}
            {/*        size="small"*/}
            {/*        value={props.user.login}*/}
            {/*    />*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <p className="titleSecond">Heslo</p>*/}
            {/*    <TextField*/}
            {/*        fullWidth={true}*/}
            {/*        variant="outlined"*/}
            {/*        size="small"*/}
            {/*        value={props.user.password}*/}
            {/*    />*/}
            {/*</div>*/}
        </div>
    );
}

export default AllNews;
