import {
    Formik,
    FormikProps,
    Form,
} from 'formik';
import * as yup from 'yup';
import {Button, CircularProgress, TextField} from "@material-ui/core";
import React from "react";
import {IUser} from "../MainContent/AllNews";

const LoginSchema = yup.object().shape({
    login: yup.string().min(3, "Too short").max(200, "Too long").required("Required"),
    password: yup.string().min(3, "Too short").max(200, "Too long").required("Required")
});

const LoginForm = (props: { user: IUser, setUser: (value: IUser) => void, isLogged: boolean }) => {
    return (
        <div className="loginContainer">
            <Formik
                initialValues={props.user}
                validationSchema={LoginSchema}
                onSubmit={(
                    values: IUser,
                    actions
                ) => {
                    props.setUser(values);
                    actions.setSubmitting(false);
                }}
            >
                {(props: FormikProps<IUser>) => {
                    const {
                        values,
                        touched,
                        errors,
                        handleBlur,
                        handleChange,
                        isSubmitting
                    } = props;
                    return (
                        <Form className="form">
                            <p className="titleSecond">Uzivatelske jmeno</p>
                            <TextField
                                type="text"
                                name="login"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.login}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.login && touched.login && errors.login}
                                error={!!(errors.login && touched.login)}
                            />

                            <p className="titleSecond">Heslo</p>
                            <TextField
                                type="text"
                                name="password"
                                fullWidth={true}
                                variant="outlined"
                                size="small"
                                value={values.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                helperText={errors.password && touched.password && errors.password}
                                error={!!(errors.password && touched.password)}
                            />

                            <div className="buttons">
                                <Button
                                    type="submit"
                                    variant="contained"
                                    style={{margin: "0.5rem", color: "var(--blue)"}}
                                    color="default"
                                >Vstopit</Button>
                            </div>
                            {isSubmitting ?
                                <div style={{
                                    display: "flex",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    margin: "2rem 0"
                                }}>
                                    <CircularProgress size={50}/>
                                </div> : null}
                        </Form>
                    );
                }}
            </Formik>
        </div>
    );
}

export default LoginForm;
