import React, {useEffect, useState} from 'react';
import News, {INews} from "../Common/News";
import moment from "moment";
import {VpnKey} from "@material-ui/icons";
import {
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle, Divider,
    TextField
} from "@material-ui/core";
import LoginForm from "../Common/LoginForm";
import InputNewsForm from "../Common/InputNewsForm";
import * as yup from "yup";

export interface IUser {
    login: string,
    password: string
}

const AllNews = () => {
    const [news, setNews] = useState<Array<INews>>([]);
    const [isLoading, setIsLoading] = useState<boolean>(true);

    useEffect(() => {
        getAllNews();
    }, []);

    const getAllNews = () => {
        fetch('http://localhost:3000/allNews')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setNews(
                    data.map((row: INews) => {
                        return {
                            id: row.id,
                            date: moment(row.date).format('DD.MM.YYYY'),
                            name: row.name,
                            content: row.content
                        }
                    })
                );
                setIsLoading(false);
            })
    }

    return (
        <div className="allNews padding">
            <h1 className="mainLabel">Novinky</h1>
            <News news={news} isLoading={isLoading}/>
            <AddNews/>
        </div>
    );
};

const AddNews = () => {
    const [openDialog, setOpenDialog] = useState<boolean>(false);
    const [user, setUser] = useState<IUser>({login: "", password: ""});
    const [isLogged, setIsLogged] = useState<boolean>(false);
    const [news, setNews] = useState<INews>({date: "", name: "", content: ""});

    const handleDialogOpen = () => {
        setOpenDialog(!openDialog);
    }

    useEffect(() => {
        fetch('http://localhost:3000/checkUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        })
            .then(response => response.json())
            .then(data => setIsLogged(data));
    }, [user]);

    return (
        <div className="addNews">
            <div>
                <Button
                    style={{backgroundColor: "white", color: "var(--fiolet)"}}
                    onClick={handleDialogOpen}
                >
                    <VpnKey/>
                </Button>
            </div>
            <Dialog open={openDialog} onClose={handleDialogOpen} className="dialog addNewsDialog">
                <DialogTitle>Pridat dalsi novinku</DialogTitle>
                <Divider />
                <DialogContent className="dialogContent">
                    {isLogged ?
                        <InputNewsForm news={news} setNews={setNews} /> :
                        <LoginForm
                            user={user}
                            setUser={setUser}
                            isLogged={isLogged}
                        />}
                    {
                        !isLogged && user.login != "" && user.password != "" &&
                        <h4 className="error">Spatny login nebo heslo</h4>
                    }
                </DialogContent>
                <Divider />
                <DialogActions className="buttons">
                    <Button
                        onClick={() => {
                            setIsLogged(false);
                            setUser({login: "", password: ""});
                        }}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Vystoupit</Button>
                    <Button
                        onClick={handleDialogOpen}
                        variant="contained"
                        style={{margin: "0.5rem", color: "var(--blue)"}}
                        color="default"
                    >Zavrit</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

const AddNewsSchema = yup.object().shape({
    login: yup.string().min(3, "Too short").max(200, "Too long").required("Required"),
    password: yup.string().min(3, "Too short").max(200, "Too long").required("Required")
});

export default AllNews;
